  `type` enum('module','plugin','block') NOT NULL DEFAULT 'module',
  `name` varchar(50) NOT NULL DEFAULT '',
  `caption` varchar(100) NOT NULL DEFAULT '',
  `multiple` enum('Y','N') NOT NULL DEFAULT 'Y',
  `useauth` enum('Y','N') NOT NULL DEFAULT 'N',
  `usecron` enum('Y','N') NOT NULL DEFAULT 'N',
  `owner` varchar(100) NOT NULL DEFAULT '',
  `version` decimal(5,4) NOT NULL DEFAULT '2.0000',
  `system` decimal(5,4) NOT NULL DEFAULT '2.0000',
  `sort` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `type_name` (`type`,`name`),
  KEY `type` (`type`),
  KEY `useauth` (`useauth`),
  KEY `sort` (`sort`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `_options`;
CREATE TABLE `_options` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL DEFAULT '',
  `var` varchar(50) NOT NULL DEFAULT '',
  `value` varchar(50) NOT NULL DEFAULT '',
  `type` varchar(10) NOT NULL DEFAULT 'string',
  PRIMARY KEY (`id`),
  KEY `var` (`var`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


